    public static void main(String[] args) {
        for (int i = 0; i < 4000; i++) {
            String html = getPageText(USA, i);
            if (html == null || html.contains("<h1>Sorry...</h1>")) {
                userrors.add(i);
            } else {
                ussongs.add(i);
            }

            String html2 = getPageText(UK, i);
            if (html2 == null || html2.contains("<h1>Sorry...</h1>")) {
                ukerrors.add(i);
            } else {
                uksongs.add(i);
            }

        }
        System.out.println(
                "US Errors: " + userrors.size() + " Songs: " + ussongs.size());

        System.out.println(
                "\n\nUS Errors: ");
        for (Integer n : userrors) {
            System.out.print(n + ",");
        }

        System.out.println(
                "\n\nUS Songs: ");
        for (Integer n : ussongs) {
            System.out.print(n + ",");
        }

        System.out.println(
                "\n\nUK Errors: " + ukerrors.size() + " Songs: " + uksongs.size());

        System.out.println(
                "\n\nUK Errors: ");
        for (Integer n : ukerrors) {
            System.out.print(n + ",");
        }

        System.out.println(
                "\n\nUK Songs: ");
        for (Integer n : uksongs) {
            System.out.print(n + ",");
        }
        
        int biggestgap = 0;
        for (int i = 0; i < songs.length - 2; i++) {
            int gap = songs[i + 1] - songs[i];
            if (gap > biggestgap) {
                biggestgap = gap;
            }
        }
        System.out.println("Biggest gap between songs: " + biggestgap);
    }

    private static String getPageText(String location, int num) {
        try {
            StringBuilder content = new StringBuilder();
            URL url = new URL(location + num);
            HttpURLConnection conn = (HttpURLConnection) url.openConnection();
            if (conn.getResponseCode() == HttpURLConnection.HTTP_MOVED_PERM || conn.getResponseCode() == HttpURLConnection.HTTP_MOVED_TEMP) {
                url = new URL(conn.getHeaderField("Location"));
            }
            try (BufferedReader in = new BufferedReader(new InputStreamReader(url.openConnection().getInputStream(), "UTF-8"))) {
                String str;
                while ((str = in.readLine()) != null) {
                    content.append(str).append("\n");
                }
            }
            return content.toString();
        } catch (Exception ex) {
            return null;
        }
    }
